import { Component } from '@angular/core';

@Component({
  selector: 'app-pop-up',
  templateUrl: './pop-up.component.html',
  styleUrls: ['./pop-up.component.scss']
})
export class PopUpComponent {

}
<div class="wrapper">
    <div class="body">
        <h2>Team details</h2>
        <div class="content">
            <p>Number of developers in the team: <span>{{members}}</span></p>
            <p>Team rating: <span>{{rating}}</span></p>
        </div>
        <button (click)="close.emit()">Close</button>
    </div>
</div>
