import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss']
})
export class HeaderComponent {
  @Input() groupName = '';
  newGroupName = '';

  editGroupName() {
    if (this.newGroupName.trim() !== '') {
      this.groupName = this.newGroupName;
      // Передайте новое название группы в другие компоненты (например, через сервис или событие)
    }
  }
}
<div class="body">
    <h1> <b>User group: </b><span> {{groupName}}</span></h1>
    <div class="edit-body">
        <input type="text" placeholder="Write new group name" [(ngModel)]="newGroupName">
        <button class="network" (click)="editGroupName()">Edit</button>
    </div>
</div>
