import { Component, Input } from '@angular/core';
import { GroupService } from '../service/group.service';

@Component({
  selector: 'app-user',
  templateUrl: './user.component.html',
  styleUrls: ['./user.component.scss']
})
export class UserComponent {
  @Input() name = '';
  @Input() status = '';
  @Input() img = '';

  constructor(private groupService: GroupService) {}

  editName(newName: string) {
    if (newName.trim() !== '') {
      this.name = newName;
      this.groupService.setGroupName(newName);
    }
  }

  editStatus(newStatus: string) {
    if (newStatus.trim() !== '') {
      this.status = newStatus;
    }
  }
}
<div class="wrapper">
    <div class="body">
        <div class="picture">
            <img [src]="img" alt="">
        </div>
        <div class="user-data">
            <div class="user-data-item">
                <h2>{{ name }}</h2>
                <div class="edit-body">
                    <input type="text" placeholder="Write new name" #newNameInput>
                    <button class="network" (click)="editName(newNameInput.value)">Edit</button>
                </div>
            </div>
            <div class="user-data-item">
                <div class="status-text">{{ status }}</div>
                <div class="edit-body">
                    <input type="text" placeholder="Write new status" #newStatusInput>
                    <button class="network" (click)="editStatus(newStatusInput.value)">Edit</button>
                </div>
            </div>
        </div>
    </div>
    <div class="group">
        <p><b>Group Name:</b><span>{{ groupService.getGroupName() }}</span></p>
    </div>
</div>
