import { Component, Input } from '@angular/core';
import { GroupService } from '../service/group.service';

@Component({
  selector: 'app-user',
  templateUrl: './user.component.html',
  styleUrls: ['./user.component.scss']
})
export class UserComponent {
  @Input() name = '';
  @Input() status = '';
  @Input() img = '';
  @Input() groupName = '';

  newUserName = '';
  newUserStatus = '';

  constructor(public groupService: GroupService) {}

  editGroupName(newGroupName: string) {
    if (newGroupName.trim() !== '') {
      this.groupService.setGroupName(newGroupName);
    }
  }

  editName() {
    if (this.newUserName.trim() !== '') {
      this.name = this.newUserName;
    }
  }

  editStatus() {
    if (this.newUserStatus.trim() !== '') {
      this.status = this.newUserStatus;
    }
  }
}
<div class="wrapper">
  <div class="body">
    <div class="picture">
      <img [src]="img" alt="">
    </div>
    <div class="user-data">
      <div class="user-data-item">
        <h2>{{ name }}</h2>
        <div class="edit-body">
          <input type="text" placeholder="Write new name">
          <button class="network">Edit</button>
        </div>
      </div>
      <div class="user-data-item">
        <div class="status-text">{{ status }}</div>
        <div class="edit-body">
          <input type="text" placeholder="Write new status">
          <button class="network">Edit</button>
        </div>
      </div>
    </div>
  </div>
  <div class="group">
    <p><b>Group Name:</b> <span>{{ groupName }}</span></p>
    <p><b>Number of group members:</b> <span>{{ members }}</span></p>
  </div>
</div>
