import { Component } from '@angular/core';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss']
})
export class HeaderComponent {

}
<div class="body">
    <h2>Development Team</h2>
    <h3 class="team-rating">
        Team rating is: 0
    </h3>
    <div class="actions">
        <button>Decrease team rating</button>
        <button>Increase team rating</button>
    </div>
</div>
